target lvl=100 resist=0.1 radius=1 pos=1,1.5 hp=3050301 particle_threshold=1016767 particle_drop_count=1.5;
options swap_delay=12 iteration=500;

fn ayaka_on_field() {
  ayaka dash;
  if .ayaka.skill.ready { ayaka skill; }
  ayaka attack, charge;
  if .ayaka.skill.ready { ayaka skill; }
  ayaka attack, charge;
  if .ayaka.skill.ready { ayaka skill; }
}

fn recharge_ayaka() {
  if .venti.skill.ready {
    venti skill, attack;
    ayaka dash, attack, charge;
  } else {
    ayaka_on_field();
  }
}

active ganyu;

while 1 {
  ganyu skill;
  # 甘雨 e 会位移, 下同
  set_player_pos(0, -4);
  ganyu burst;
  if .venti.skill.ready { venti skill; }
  venti burst;
  mona burst, skill;
  # 上面位移过, 冲刺回近战距离用技能
  ayaka dash;
  set_player_pos(0, 0);
  if .ayaka.skill.ready { ayaka skill; }
  ayaka attack, burst;
  ganyu skill;
  set_player_pos(0, -4);
  ganyu aim;
  venti skill, attack;
  ganyu aim:2;
  # 假设蓄力箭过程中移动回原位
  set_player_pos(0, 0);
  ayaka skill, dash, attack, charge;

  # 因为循环开始的流程还有很多 e 技能打球, 这里充能的判断阈值给很低
  while .ayaka.energy + 30 < .ayaka.energymax {
    recharge_ayaka();
  }
}

